--  DDL for Table PRESTAMO
--------------------------------------------------------

  CREATE TABLE Prestamo (

    ID_Prestamo NUMERIC (10,0) NOT NULL,
    ID_Deudor NUMERIC (10,0) NOT NULL,
    ID_Banco NUMERIC (10,0) NOT NULL,
    Fecha TIMESTAMP DEFAULT SYSDATE,
    Valor_Otorgado NUMERIC (20,0) DEFAULT 0,
    Pagado VARCHAR2 (2) DEFAULT 'NO',
    
    PRIMARY KEY (ID_Prestamo),
    FOREIGN KEY (ID_Banco) references Banco,
    FOREIGN KEY (ID_Deudor) references Deudor,
    UNIQUE (ID_Banco, ID_Deudor, Fecha),
    CHECK (Pagado IN ('SI', 'NO')),
    CHECK (Valor_Otorgado >= 0)
);
