--  DDL for View VALOR_PRESTAMO_AÑO_V1
--------------------------------------------------------

CREATE OR REPLACE EDITIONABLE VIEW "VALOR_PRESTAMO_AÑO_V1" ("AÑO", "TOTAL") AS select extract(year from Fecha) as Año,sum(valor_otorgado) as Total
from Prestamo
group by extract(year from Fecha);
--------------------------------------------------------
--  DDL for View VALOR_PRESTAMO_MENSUAL_BANCO_V2
--------------------------------------------------------

  CREATE OR REPLACE EDITIONABLE VIEW "VALOR_PRESTAMO_MENSUAL_BANCO_V2" ("AÑO", "MES", "NOMBRE", "TOTAL") AS select Extract (year from prestamo.Fecha) as Año,Extract(Month from prestamo.Fecha)as Mes, Banco.nombre,sum(prestamo.valor_otorgado)as Total
from Banco,prestamo
where banco.id_banco=prestamo.id_banco
group by banco.nombre,Extract (year from prestamo.Fecha),Extract (Month from prestamo.Fecha);
--------------------------------------------------------
--  DDL for View SALDO_PRESTAMO_V3
--------------------------------------------------------

  CREATE OR REPLACE EDITIONABLE VIEW "SALDO_PRESTAMO_V3" ("NOMBRE", "VALOR_PRESTAMO", "VALOR_ABONADO", "DIFERENCIA") AS With Abono_Total (ID_Prest, Valor_Prestado, Valor_Abonado) AS
(Select P.ID_PRESTAMO, P.Valor_Otorgado, SUM (A.VALOR_ABONO)
From Prestamo P INNER JOIN Abono A ON P.ID_PRESTAMO = A.ID_PRESTAMO
Group By P.ID_Prestamo, P.Valor_Otorgado)
Select D.Nombre Nombre, A.Valor_Prestado Valor_Prestamo, A.Valor_Abonado Valor_Abonado, (A.Valor_Prestado - A.Valor_Abonado) Diferencia
From Deudor D INNER JOIN Prestamo P ON D.ID_Deudor = P.ID_Deudor INNER JOIN Abono_Total A ON P.ID_Prestamo = A.ID_Prest;
--------------------------------------------------------
--  DDL for View INFO_DEUDOR_V4
--------------------------------------------------------

  CREATE OR REPLACE EDITIONABLE VIEW "INFO_DEUDOR_V4" ("NOMBRE", "DOCUMENTO", "BANCO", "FECHA", "VALOR_DEUDA") AS select deudor.nombre Nombre,deudor.numero_doc Documento,banco.nombre Banco,prestamo.fecha Fecha,prestamo.valor_otorgado Valor_Deuda
from deudor,banco,prestamo
where deudor.id_deudor=prestamo.id_deudor and prestamo.id_banco=banco.id_banco
order by deudor.tipo_doc,deudor.numero_doc;
--------------------------------------------------------
--  DDL for View CANTIDAD_PRESTAMO_V5
--------------------------------------------------------

  CREATE OR REPLACE EDITIONABLE VIEW "CANTIDAD_PRESTAMO_V5" ("NOMBRE", "TOTAL_DEUDA") AS select deudor.nombre,nvl((select sum(prestamo.valor_otorgado)from prestamo where deudor.id_deudor=prestamo.id_deudor group by deudor.nombre),0) as Total_deuda
