--------------------------------------------------------
--  DDL for View DEUDOR_TOTAL_BANCOS_V7
--------------------------------------------------------

  CREATE OR REPLACE EDITIONABLE VIEW "DEUDOR_TOTAL_BANCOS_V7" ("NOMBRE") AS with conteo_bancos(cantidad)as (select count(*) from Banco),
Conteo_Deuda(ID,Cantidad) as (select prestamo.id_deudor,count(prestamo.id_banco)from prestamo group by prestamo.id_deudor)
select deudor.nombre
from deudor,conteo_bancos,conteo_deuda
where conteo_bancos.cantidad=conteo_deuda.Cantidad and deudor.id_deudor=Conteo_deuda.ID;
--------------------------------------------------------
--  DDL for View PROMEDIO_BANCO_V8
--------------------------------------------------------

  CREATE OR REPLACE EDITIONABLE VIEW "PROMEDIO_BANCO_V8" ("NOMBRE", "AÑO", "PROMEDIO_BANCO", "PROMEDIO_GENERAL") AS with promedio_general(año,num)as( 
select extract(year from p.fecha) as año ,avg(p.id_deudor) as promedio_prestamos
from prestamo p, banco b
where p.id_banco=b.id_banco
group by extract(year from p.fecha)
order by extract(year from p.fecha)
), promedio_banco(id,año,num) as(
select p.id_banco,extract(year from p.fecha) as año, avg(p.id_deudor) as promedio
from prestamo p
group by p.id_banco,extract(year from p.fecha)
order by p.id_banco
)
select b.nombre,pg.año,round (pb.num,3) as promedio_banco,round(pg.num,3) as promedio_general
from banco b,promedio_general pg, promedio_banco pb
where pg.año=pb.año
and pb.id=b.id_banco
and pb.num>pg.num
order by b.nombre, pg.año;
--------------------------------------------------------
--  DDL for View PRESTAMOS_GENERO_V10
--------------------------------------------------------

  CREATE OR REPLACE EDITIONABLE VIEW "PRESTAMOS_GENERO_V10" ("AÑO", "MES", "FEMENINO", "MASCULINO", "TOTAL") AS select Año,Mes,Femenino,Masculino,Masculino+Femenino AS total
from
(
select 
nvl(to_char(prestamo.fecha,'YYYY'),'Total') as Año,
nvl(to_char(prestamo.fecha,'MM'),'Total') as Mes,
sum(case when deudor.genero='M' then 1 else 0 end)as Masculino,
sum(case when deudor.genero='F' then 1 else 0 end)as Femenino
from prestamo,deudor
where  prestamo.id_deudor=deudor.id_deudor
group by rollup (to_char(prestamo.fecha,'YYYY'),to_char(prestamo.fecha,'MM')));
