SET P.Pagado = 'SI'
WHERE P.Valor_Otorgado <= (SELECT SUM(A.VALOR_ABONO)
                        FROM ABONO A
                        WHERE P.ID_PRESTAMO = A.ID_PRESTAMO)
                        
